import { Component } from '@angular/core';

@Component({
  selector: 'app-successful-pay',
  templateUrl: './successfulPay.component.html',
  styleUrls: ['./successfulPay.component.css']
})
export class SuccessfulPayComponent {

}
<div class="mainBoxPay"><!-- Div que contiene a el logo,texto y boton  -->
    <div class="logoPay">
        <p>KOAJ</p>
    </div>
    <div class="textPay"><!--Div que contiene el texto de pago -->
        <p>Thank you for choosing our online store as your shopping destination! With your support, you help us grow and continue to offer exceptional products and services.</p>

        <p>Enjoy your purchase, and we hope to see you again soon in our online store!</p> 

    </div>
    <div class="buttomPay"><!--Div que contiene el boton de pago -->
        <a href="#">Go back</a><!--Numero de error-->
    </div>
